    WRITE_COMPLETE_FROM_EMAIL_ADDRESS = 64
    WRITE_COMPLETE_CC_EMAIL_ADDRESS = 128
    WRITE_COMPLETE_BCC_EMAIL_ADDRESS = 256
    RENDER_CALENDAR_EVENT = 512
    SKIP_BYTE_ORDER_MARK_IN_BODY = 1024
    RENDER_VCARD_INFO = 2048
    DISPLAY_AS_OUTLOOK = 4096
    RENDER_TASK_FIELDS = 8192
